import React, { useEffect, useState } from "react";

const ProductFilterPage = () => {
  const [products, setProducts] = useState([]);
  const [filterOptions, setFilterOptions] = useState({ categories: [], brands: [] });
  const [filters, setFilters] = useState({
    category: [],
    brandName: [],
    minPrice: "",
    maxPrice: ""
  });

  // Fetch filter dropdown values
  const fetchFilterOptions = async () => {
    const res = await fetch("/api/filter-options");
    const data = await res.json();
    setFilterOptions({ categories: data.categories, brands: data.brands });
  };

  // Fetch products with current filters
  const fetchFilteredProducts = async () => {
    const res = await fetch("/api/filter-product", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(filters),
    });
    const data = await res.json();
    setProducts(data.data || []);
  };

  useEffect(() => {
    fetchFilterOptions();
    fetchFilteredProducts(); // Initial load
  }, []);

  // On filter change
  useEffect(() => {
    fetchFilteredProducts();
  }, [filters]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleMultiSelect = (e, name) => {
    const options = Array.from(e.target.selectedOptions).map((opt) => opt.value);
    setFilters((prev) => ({ ...prev, [name]: options }));
  };

  return (
    <div className="p-4">
      <h2 className="text-lg font-bold mb-4">Filter Products</h2>

      {/* Filter Form */}
      <div className="flex flex-wrap gap-4 mb-6">
        <select multiple name="category" onChange={(e) => handleMultiSelect(e, "category")}>
          {filterOptions.categories.map((cat) => (
            <option key={cat}>{cat}</option>
          ))}
        </select>

        <select multiple name="brandName" onChange={(e) => handleMultiSelect(e, "brandName")}>
          {filterOptions.brands.map((brand) => (
            <option key={brand}>{brand}</option>
          ))}
        </select>

        <input
          type="number"
          name="minPrice"
          placeholder="Min Price"
          onChange={handleChange}
        />
        <input
          type="number"
          name="maxPrice"
          placeholder="Max Price"
          onChange={handleChange}
        />
      </div>

      {/* Products Grid */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        {products.map((product) => (
          <div key={product._id} className="border p-2 rounded">
            <img src={product.productImage[0]} alt="" className="h-32 w-full object-cover" />
            <h3>{product.productName}</h3>
            <p>{product.brandName}</p>
            <p>${product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductFilterPage;
