import React, { useEffect, useState } from 'react'
import SummaryApi from '../common'
import moment from 'moment'
import displayINRCurrency from '../helpers/displayCurrency'
import { FaShoppingCart, FaCog, FaTruck, FaCheckCircle } from 'react-icons/fa'

const statusSteps = [
  { label: "ordered", icon: FaShoppingCart },
  { label: "Processing", icon: FaCog },
  { label: "dispatched", icon: FaTruck },
  { label: "Delivered", icon: FaCheckCircle }
]

const getStatusIndex = (status) =>
  statusSteps.findIndex(step => step.label.toLowerCase() === status.toLowerCase())

const getStatusLabelColor = (status) => {
  switch (status.toLowerCase()) {
    case 'ordered':
      return 'bg-gray-100 text-gray-800 border border-gray-300';
    case 'processing':
      return 'bg-yellow-100 text-yellow-800 border border-yellow-300';
    case 'dispatched':
      return 'bg-blue-100 text-blue-800 border border-blue-300';
    case 'delivered':
      return 'bg-green-100 text-green-800 border border-green-300';
    default:
      return 'bg-gray-100 text-gray-700 border border-gray-200';
  }
};

const OrderSummaryCard = ({ order, onViewDetails, isExpanded }) => (
  <div className="bg-white rounded-xl shadow-lg border border-gray-100 flex flex-col w-full max-w-md mx-auto mb-8 transition hover:shadow-xl">
    <div className="p-6 flex flex-col gap-3">
      <div className="flex justify-between items-center">
        <span className="text-xs text-gray-400 font-medium tracking-wide">
          Order ID: <span className="font-semibold text-gray-700">{order._id}</span>
        </span>
        <span
          className={`px-3 py-1 rounded-full text-xs font-semibold ${getStatusLabelColor(order.status)}`}
        >
          {order.status}
        </span>
      </div>
      <span className="text-sm text-gray-600">{moment(order.createdAt).format('LL')}</span>
      <div className="flex items-center gap-3 mt-2">
        {order.productDetails.map((product, idx) => (
          product.image[0] && (
            <img
              key={product.productId + idx}
              src={product.image[0]}
              alt={product.name}
              className="w-16 h-16 rounded-lg bg-white object-cover border border-gray-200 shadow-sm"
              style={{ objectFit: 'cover' }}
            />
          )
        ))}
      </div>
      <div className="flex justify-between items-center mt-4">
        <span className="text-base font-semibold text-gray-700">Total</span>
        <span className="text-lg font-bold text-gray-900">{displayINRCurrency(order.totalAmount)}</span>
      </div>
      <button
        className="mt-4 px-6 py-2 rounded-full bg-black text-white font-semibold tracking-wide shadow hover:bg-gray-900 transition"
        onClick={onViewDetails}
      >
        {isExpanded ? "Hide Details" : "View Details"}
      </button>
    </div>
  </div>
)

const OrderDetails = ({ order }) => {
  const currentStatusIndex = getStatusIndex(order.status)
  return (
    <div className="bg-gray-50 p-6 rounded-xl shadow-inner mb-8 border border-gray-100">
      <div className="mb-6">
        <h3 className="font-semibold text-lg text-gray-800 mb-3 border-b border-gray-200 pb-2">Products</h3>
        <div className="grid gap-4">
          {order.productDetails.map((product, idx) => (
            <div key={product.productId + idx} className="flex gap-4 bg-white rounded-lg p-3 shadow-sm border border-gray-100">
              <div className="flex gap-2">
                {product.image.map((imgUrl, i) => (
                  <img
                    key={imgUrl + i}
                    src={imgUrl}
                    className="w-16 h-16 bg-gray-100 object-cover rounded-lg border border-gray-200"
                    alt={product.name + ' image ' + (i + 1)}
                  />
                ))}
              </div>
              <div className="flex flex-col justify-center">
                <div className="font-medium text-gray-900 text-base">{product.name}</div>
                <div className="flex items-center gap-4 mt-2">
                  <div className="text-base text-red-500 font-semibold">{displayINRCurrency(product.price)}</div>
                  <div className="text-sm text-gray-600">Qty: {product.quantity}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Track Your Order */}
      <div className="mt-2 mb-6 p-4 bg-white rounded-lg border border-gray-100">
        <h3 className="font-semibold text-base mb-4 text-gray-800">Order Status</h3>
        <div className="flex items-center gap-2">
          {statusSteps.map((step, idx) => {
            const Icon = step.icon
            const isActive = idx === currentStatusIndex
            const isCompleted = idx < currentStatusIndex
            return (
              <React.Fragment key={step.label}>
                <div className="flex flex-col items-center">
                  <div
                    className={`w-9 h-9 flex items-center justify-center rounded-full border-2
                      ${isCompleted || isActive
                        ? "bg-green-600 text-white border-green-600"
                        : "bg-gray-200 text-gray-400 border-gray-200"}
                      transition-colors duration-300`}
                  >
                    <Icon size={18} />
                  </div>
                  <span className={`text-xs mt-1 text-center w-20 
                    ${isCompleted || isActive ? "text-green-700 font-semibold" : "text-gray-400"}`}>
                    {step.label}
                  </span>
                </div>
                {idx < statusSteps.length - 1 && (
                  <div
                    className={`flex-1 h-1 mx-1 rounded transition-colors duration-300
                      ${idx < currentStatusIndex ? "bg-green-600" : "bg-gray-200"}`}
                    style={{ minWidth: 24 }}
                  ></div>
                )}
              </React.Fragment>
            )
          })}
        </div>
        <div className="mt-3 text-sm text-gray-700">
          Current Status: <span className="font-bold">{order.status}</span>
        </div>
      </div>

      {/* Shipping Details */}
      <div className="mb-6">
        <h3 className="text-base font-semibold text-gray-800 mb-2">Shipping Details</h3>
        {order.shippingAddress && (
          <div className="bg-white rounded-lg p-4 border border-gray-100">
            <div className="font-medium text-gray-700">Address:</div>
            <div className="text-gray-900">{order.shippingAddress.name}</div>
            <div className="text-gray-700">{order.shippingAddress.addressLine1}</div>
            {order.shippingAddress.addressLine2 && <div className="text-gray-700">{order.shippingAddress.addressLine2}</div>}
            <div className="text-gray-700">
              {order.shippingAddress.city}, {order.shippingAddress.state} - {order.shippingAddress.postalCode}
            </div>
            <div className="text-gray-700">{order.shippingAddress.country}</div>
            <div className="text-gray-700">Phone: {order.shippingAddress.phone}</div>
          </div>
        )}
      </div>

      <div className="flex justify-end">
        <div className="font-bold text-lg text-gray-900">
          Total Amount: {displayINRCurrency(order.totalAmount)}
        </div>
      </div>
    </div>
  )
}

const OrderPage = () => {
  const [data, setData] = useState([])
  const [expandedIndex, setExpandedIndex] = useState(null)

  const fetchOrderDetails = async () => {
    const response = await fetch(SummaryApi.getOrder.url, {
      method: SummaryApi.getOrder.method,
      credentials: 'include'
    })
    const responseData = await response.json()
    setData(responseData.data)
  }

  useEffect(() => {
    fetchOrderDetails()
  }, [])

  return (
    <div className="bg-gray-50 min-h-screen py-10">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-2xl font-bold text-gray-900 mb-8 tracking-tight">My Orders</h1>
        {
          !data[0] && (
            <p className="text-center mt-10 text-gray-400">No Order available</p>
          )
        }
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {data.map((item, index) => (
            <div key={item._id + index}>
              <OrderSummaryCard
                order={item}
                isExpanded={expandedIndex === index}
                onViewDetails={() => setExpandedIndex(expandedIndex === index ? null : index)}
              />
              {expandedIndex === index && (
                <OrderDetails order={item} />
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  )
}

export default OrderPage
