import React from "react";

const IndianwearCards = () => {
  const categories = [
    {
      title: "CORPORATE COOL",
      image: "/images/corporate-cool.png", // replace with actual image path
    },
    {
      title: "KURTAS & SUIT SETS",
      image: "/images/kurtas.png",
    },
    {
      title: "TOPS & TEES",
      image: "/images/tops.png",
    },
    {
      title: "DRESSES",
      image: "/images/dresses.png",
    },
  ];

  return (
    <section className="bg-white py-10 px-4">
      <div className="max-w-7xl mx-auto grid grid-cols-2 sm:grid-cols-4 gap-4">
        {categories.map((item, index) => (
          <div
            key={index}
            className="rounded-2xl overflow-hidden relative group cursor-pointer"
          >
            <img
              src={item.image}
              alt={item.title}
              className="w-full h-[300px] object-cover transition-transform duration-300 group-hover:scale-105"
            />
            <div className="absolute bottom-0 w-full bg-gradient-to-t from-black/70 to-transparent px-4 py-3">
              <h3 className="text-white text-sm sm:text-base font-semibold text-center">
                {item.title}
              </h3>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default IndianwearCards;
