import React from "react";

const categories = [
  {
    label: "MENSWEAR",
    img: "https://assets.tatacliq.com/medias/sys_master/images/64927657099294.jpg",
  },
  {
    label: "KIDSWEAR",
    img: "https://assets.tatacliq.com/medias/sys_master/images/Kidswear.jpg",
  },
  {
    label: "WOMENSWEAR",
    img: "https://assets.tatacliq.com/medias/sys_master/images/Womenswear.jpg",
  },
  {
    label: "SUNGLASSES",
    img: "https://assets.tatacliq.com/medias/sys_master/images/Sunglasses.jpg",
  },
  {
    label: "EARBUDS",
    img: "https://assets.tatacliq.com/medias/sys_master/images/Earbuds.jpg",
  },
  {
    label: "FOOTWEAR",
    img: "https://assets.tatacliq.com/medias/sys_master/images/Footwear.jpg",
  },
  {
    label: "WATCHES",
    img: "https://assets.tatacliq.com/medias/sys_master/images/Watches.jpg",
  },
  {
    label: "HANDBAG",
    img: "https://assets.tatacliq.com/medias/sys_master/images/Handbags.jpg",
  },
];

function CategoryCards() {
  return (
    <div className="w-full px-8 py-8 bg-white">
      <div className="grid grid-cols-2 sm:grid-cols-4 lg:grid-cols-8 gap-6">
        {categories.map((category, index) => (
          <div key={index} className="flex flex-col items-center">
            <div className="w-28 h-28 rounded-xl overflow-hidden shadow-lg bg-gray-100">
              <img
                src={category.img}
                alt={category.label}
                className="w-full h-full object-cover"
              />
            </div>
            <p className="text-sm mt-3 text-center text-[#541B1B] font-semibold tracking-wide">
              {category.label}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CategoryCards;
