import React, { useContext, useEffect, useState } from 'react'
import SummaryApi from '../common'
import Context from '../context'
import displayINRCurrency from '../helpers/displayCurrency'
import { MdDelete } from "react-icons/md";
import { loadStripe } from '@stripe/stripe-js';
import addToCart from '../helpers/addToCart'

const Wishlist = () => {
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const context = useContext(Context)
    const loadingWishlist = new Array(4).fill(null)

    const fetchData = async () => {
        const response = await fetch(SummaryApi.addToWishlistProductView.url, {
            method: SummaryApi.addToWishlistProductView.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
        })
        const responseData = await response.json()
        if (responseData.success) {
            setData(responseData.data)
        }
    }

    const handleLoading = async () => {
        await fetchData()
    }

    useEffect(() => {
        setLoading(true)
        handleLoading().finally(() => setLoading(false))
    }, [])

    const { fetchUserAddToCart } = useContext(Context)
    const handleAddToCart = async (e, id) => {
        e.preventDefault()
        await addToCart(e, id)
        fetchUserAddToCart()
    }

    const deleteWishlistProduct = async (id) => {
        const response = await fetch(SummaryApi.deleteWishlistProduct.url, {
            method: SummaryApi.deleteWishlistProduct.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({ _id: id })
        })
        const responseData = await response.json()
        if (responseData.success) {
            fetchData()
            context.fetchUserAddToWishlist()
        }
    }

    const totalQty = data.reduce((previousValue, currentValue) => previousValue + currentValue.quantity, 0)
    const totalPrice = data.reduce((preve, curr) => preve + (curr.quantity * (curr?.productId?.sellingPrice || 0)), 0)

    return (
        <div className="container mx-auto py-6">
            <div className="text-center text-lg my-3">
                {data.length === 0 && !loading && (
                    <p className="bg-white py-5 rounded shadow">No Data</p>
                )}
            </div>

            <div className="flex flex-col lg:flex-row gap-10 lg:justify-between p-4">
                {/* Product List */}
                <div className="w-full max-w-3xl">
                    {loading ? (
                        loadingWishlist.map((_, idx) => (
                            <div key={idx} className="w-full bg-slate-200 h-32 my-2 border border-slate-300 animate-pulse rounded-lg shadow" />
                        ))
                    ) : (
                        data.map((product, idx) => {
                            if (!product?.productId) return null
                            return (
                                <div
                                    key={product?._id}
                                    className="w-full bg-white h-36 my-4 rounded-lg shadow hover:shadow-lg transition-all duration-200 flex items-stretch relative"
                                >
                                    {/* Image */}
                                    <div className="w-36 h-36 bg-gray-100 flex-shrink-0 flex items-center justify-center rounded-l-lg overflow-hidden">
                                        <img
                                            src={product?.productId?.productImage?.[0]}
                                            alt={product?.productId?.productName}
                                            className="w-full h-full object-contain"
                                        />
                                    </div>
                                    {/* Info */}
                                    <div className="flex-1 px-6 py-3 flex flex-col justify-between">
                                        {/* Delete Icon */}
                                        <button
                                            className="absolute top-2 right-2 text-gray-400 hover:text-red-600 transition-colors"
                                            onClick={() => deleteWishlistProduct(product?._id)}
                                            aria-label="Remove from wishlist"
                                        >
                                            <MdDelete size={22} />
                                        </button>
                                        {/* Product Name */}
                                        <h2 className="text-lg font-semibold text-gray-800 truncate">
                                            {product?.productId?.productName || "No name"}
                                        </h2>
                                        {/* Category & Brand */}
                                        <div className="flex gap-3 text-xs text-gray-500 mt-1">
                                            <span className="capitalize">{product?.productId?.category || "No category"}</span>
                                            <span className="capitalize">{product?.productId?.brandName || "No brand"}</span>
                                        </div>
                                        {/* Price & Add to Cart */}
                                        <div className="flex items-end justify-between mt-3">
                                            <div>
                                                <div className="text-red-600 font-bold text-lg">
                                                    {displayINRCurrency(product?.productId?.sellingPrice || 0)}
                                                </div>
                                                <div className="text-gray-500 text-sm">
                                                    Qty: {product?.quantity || 1}
                                                </div>
                                            </div>
                                            <button
                                                className="bg-black hover:bg-gray-800 text-white font-medium px-6 py-2 rounded-full transition-all text-sm shadow"
                                                onClick={(e) => handleAddToCart(e, product?.productId?._id)}
                                            >
                                                Add to Cart
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            )
                        })
                    )}
                </div>
                {/* (Optional) Summary Section */}
                {/* <div className="w-full max-w-sm mt-5 lg:mt-0">
                    <div className="bg-white rounded-lg shadow p-4">
                        <h2 className="text-lg font-bold mb-2">Summary</h2>
                        <div className="flex justify-between text-gray-700 mb-1">
                            <span>Quantity</span>
                            <span>{totalQty}</span>
                        </div>
                        <div className="flex justify-between text-gray-700 mb-3">
                            <span>Total Price</span>
                            <span>{displayINRCurrency(totalPrice)}</span>
                        </div>
                        <button
                            className="bg-blue-600 hover:bg-blue-700 text-white w-full py-2 rounded-full font-semibold"
                            onClick={handlePayment}
                        >
                            Payment
                        </button>
                    </div>
                </div> */}
            </div>
        </div>
    )
}

export default Wishlist
