import React from "react";

const offerData = [
  {
    brand: "U.S. POLO ASSN.",
    description: "MIN. 40% OFF + 20% OFF",
    image: "/mnt/data/633a31c7-2113-43b5-9d33-29923604d722.png",
  },
  {
    brand: "WOODLAND",
    description: "30% - 60% OFF",
    image: "/mnt/data/633a31c7-2113-43b5-9d33-29923604d722.png",
  },
  {
    brand: "TITAN",
    description: "UP TO 50% OFF",
    image: "/mnt/data/633a31c7-2113-43b5-9d33-29923604d722.png",
  },
  {
    brand: "PUMA",
    description: "MIN. 50% OFF",
    image: "/mnt/data/633a31c7-2113-43b5-9d33-29923604d722.png",
  },
];

const OfferCard = ({ brand, description, image }) => (
  <div className="w-60 rounded-2xl overflow-hidden shadow-md bg-white">
    <img src={image} alt={brand} className="w-full h-52 object-cover" />
    <div className="p-4 text-center">
      <h3 className="text-lg font-semibold mb-1">{brand}</h3>
      <p className="text-sm text-gray-700">{description}</p>
    </div>
  </div>
);

const TataCliqOffers = () => {
  return (
    <div className="flex flex-wrap gap-4 justify-center">
      {offerData.map((offer, idx) => (
        <OfferCard key={idx} {...offer} />
      ))}
    </div>
  );
};

export default TataCliqOffers;
