import React, { useState } from 'react';
import { MdModeEditOutline } from 'react-icons/md';
import { FaHeart, FaStar } from 'react-icons/fa'; // Added for heart and rating
import AdminEditProduct from './AdminEditProduct';
import displayINRCurrency from '../helpers/displayCurrency';

const AdminProductCard = ({ data, fetchdata }) => {
  const [editProduct, setEditProduct] = useState(false);

  // Calculate discount percentage
  const discount = Math.round(
    ((data?.price - data?.sellingPrice) / data?.price) * 100
  );

  return (
    <div className="relative bg-white rounded-lg shadow hover:shadow-lg transition-all w-60 p-2">
      {/* --- Top Icons --- */}
      <div className="absolute top-2 right-2 flex flex-col items-center gap-2 z-10">
        <button className="bg-white rounded-full p-1 shadow hover:bg-red-100">
          <FaHeart className="text-gray-500" />
        </button>
        <div
          className="bg-white rounded-full p-1 shadow hover:bg-green-100 cursor-pointer"
          onClick={() => setEditProduct(true)}
        >
          <MdModeEditOutline className="text-gray-500" />
        </div>
      </div>

      {/* --- Image --- */}
      <div className="w-full h-64 rounded-md overflow-hidden">
        <img
          src={data?.productImage[0]}
          alt={data?.productName}
          className="w-full h-full object-cover"
        />
      </div>

      {/* --- Product Info --- */}
      <div className="mt-3 px-1">
        <h3 className="text-sm text-gray-900 font-semibold leading-tight">
          {data?.brandName || 'Brand'}
        </h3>
        <p className="text-sm text-gray-700 line-clamp-2">
          {data?.productName}
        </p>

        {/* --- Price Info --- */}
        <div className="mt-1 flex items-center gap-2">
          <span className="font-semibold text-black">
            {displayINRCurrency(data?.sellingPrice)}
          </span>
          <span className="line-through text-gray-400 text-sm">
            {displayINRCurrency(data?.price)}
          </span>
          <span className="text-green-600 text-sm font-medium">
            {discount}% off
          </span>
        </div>

        
        {/* --- Limited Stock Text (Conditional) --- */}
        {data?.stock < 10 && (
          <p className="text-xs text-red-600 font-semibold mt-1">
            Limited stock!
          </p>
        )}
      </div>

      {/* --- Edit Product Modal --- */}
      {editProduct && (
        <AdminEditProduct
          productData={data}
          onClose={() => setEditProduct(false)}
          fetchdata={fetchdata}
        />
      )}
    </div>
  );
};

export default AdminProductCard;
