import React, { useState } from 'react'
import { CgClose } from "react-icons/cg";
import productCategory from '../helpers/productCategory';
import { FaCloudUploadAlt } from "react-icons/fa";
import uploadImage from '../helpers/uploadImage';
import DisplayImage from './DisplayImage';
import { MdDelete } from "react-icons/md";
import SummaryApi from '../common';
import {toast} from 'react-toastify'

const AdminEditProduct = ({
    onClose,
    productData,
    fetchdata
  }) => {

  const [data,setData] = useState({
    ...productData,
    productName : productData?.productName,
    brandName : productData?.brandName,
    category : productData?.category,
    productImage : productData?.productImage || [],
    description : productData?.description,
    price : productData?.price,
    sellingPrice : productData?.sellingPrice
  })
  const [openFullScreenImage,setOpenFullScreenImage] = useState(false)
  const [fullScreenImage,setFullScreenImage] = useState("")


  const handleOnChange = (e)=>{
      const { name, value} = e.target

      setData((preve)=>{
        return{
          ...preve,
          [name]  : value
        }
      })
  }

  const handleUploadProduct = async(e) => {
    const file = e.target.files[0]
    const uploadImageCloudinary = await uploadImage(file)

    setData((preve)=>{
      return{
        ...preve,
        productImage : [ ...preve.productImage, uploadImageCloudinary.url]
      }
    })
  }

  const handleDeleteProductImage = async(index)=>{
    console.log("image index",index)
    
    const newProductImage = [...data.productImage]
    newProductImage.splice(index,1)

    setData((preve)=>{
      return{
        ...preve,
        productImage : [...newProductImage]
      }
    })
    
  }


  {/**upload product */}
  const handleSubmit = async(e) =>{
    e.preventDefault()
    
    const response = await fetch(SummaryApi.updateProduct.url,{
      method : SummaryApi.updateProduct.method,
      credentials : 'include',
      headers : {
        "content-type" : "application/json"
      },
      body : JSON.stringify(data)
    })

    const responseData = await response.json()

    if(responseData.success){
        toast.success(responseData?.message)
        onClose()
        fetchdata()
    }


    if(responseData.error){
      toast.error(responseData?.message)
    }
  

  }

  return (
   <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
  <div className="bg-white rounded-2xl shadow-lg w-full max-w-3xl h-full max-h-[90vh] overflow-hidden flex flex-col">

    {/* Header */}
    <div className="flex justify-between items-center px-6 py-4 border-b">
      <h2 className="text-xl font-semibold text-gray-800">Edit Product</h2>
      <button onClick={onClose} className="text-gray-600 hover:text-red-500 text-2xl">
        <CgClose />
      </button>
    </div>

    {/* Form */}
    <form
      className="flex-1 overflow-y-auto p-6 space-y-4"
      onSubmit={handleSubmit}
    >
      {/* Product Name */}
      <div>
        <label className="block text-sm text-gray-600 mb-1">Product Name</label>
        <input
          type="text"
          name="productName"
          value={data.productName}
          onChange={handleOnChange}
          placeholder="Enter product name"
          className="w-full p-2 border rounded-md bg-gray-50 focus:outline-none focus:ring-2 focus:ring-red-400"
          required
        />
      </div>

      {/* Brand Name */}
      <div>
        <label className="block text-sm text-gray-600 mb-1">Brand Name</label>
        <input
          type="text"
          name="brandName"
          value={data.brandName}
          onChange={handleOnChange}
          placeholder="Enter brand name"
          className="w-full p-2 border rounded-md bg-gray-50 focus:outline-none focus:ring-2 focus:ring-red-400"
          required
        />
      </div>

      {/* Category */}
      <div>
        <label className="block text-sm text-gray-600 mb-1">Category</label>
        <select
          name="category"
          value={data.category}
          onChange={handleOnChange}
          className="w-full p-2 border rounded-md bg-gray-50"
          required
        >
          <option value="">Select Category</option>
          {productCategory.map((el, index) => (
            <option key={el.value + index} value={el.value}>
              {el.label}
            </option>
          ))}
        </select>
      </div>

      {/* Product Images */}
      <div>
        <label className="block text-sm text-gray-600 mb-1">Product Images</label>
        <div className="space-y-3">
          {data.productImage.map((url, index) => (
            <div key={index} className="flex items-center gap-3">
              <input
                type="text"
                value={url}
                className="flex-1 p-2 border rounded-md bg-gray-50"
                placeholder="Enter image URL"
                onChange={(e) => {
                  const updatedImages = [...data.productImage];
                  updatedImages[index] = e.target.value;
                  setData((prev) => ({
                    ...prev,
                    productImage: updatedImages
                  }));
                }}
              />
              <img
                src={url}
                alt={`Preview ${index}`}
                className="w-16 h-16 object-cover rounded cursor-pointer border"
                onClick={() => {
                  setOpenFullScreenImage(true);
                  setFullScreenImage(url);
                }}
              />
              <button
                type="button"
                onClick={() => handleDeleteProductImage(index)}
                className="text-red-500 hover:text-red-700 text-xl"
              >
                <MdDelete />
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={() =>
              setData((prev) => ({
                ...prev,
                productImage: [...prev.productImage, ""]
              }))
            }
            className="text-blue-600 text-sm font-medium hover:underline"
          >
            + Add Image URL
          </button>
        </div>
      </div>

      {/* Price */}
      <div>
        <label className="block text-sm text-gray-600 mb-1">Price</label>
        <input
          type="number"
          name="price"
          value={data.price}
          onChange={handleOnChange}
          placeholder="Enter price"
          className="w-full p-2 border rounded-md bg-gray-50"
          required
        />
      </div>

      {/* Selling Price */}
      <div>
        <label className="block text-sm text-gray-600 mb-1">Selling Price</label>
        <input
          type="number"
          name="sellingPrice"
          value={data.sellingPrice}
          onChange={handleOnChange}
          placeholder="Enter selling price"
          className="w-full p-2 border rounded-md bg-gray-50"
          required
        />
      </div>

      {/* Description */}
      <div>
        <label className="block text-sm text-gray-600 mb-1">Description</label>
        <textarea
          name="description"
          value={data.description}
          onChange={handleOnChange}
          placeholder="Enter product description"
          rows={4}
          className="w-full p-2 border rounded-md bg-gray-50 resize-none"
        ></textarea>
      </div>

      {/* Submit Button */}
      <div className="text-right mt-4">
        <button
          type="submit"
          className="bg-red-600 hover:bg-red-700 text-white font-semibold px-6 py-2 rounded-full"
        >
          Update Product
        </button>
      </div>
    </form>

    {/* Full screen image */}
    {openFullScreenImage && (
      <DisplayImage
        imgUrl={fullScreenImage}
        onClose={() => setOpenFullScreenImage(false)}
      />
    )}
  </div>
</div>

  )
}

export default AdminEditProduct
