import React from 'react';
import { Link, useLocation, useNavigate, Outlet } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart, faShoppingCart, faSearch,faCircleUser } from '@fortawesome/free-solid-svg-icons';
import { useDispatch, useSelector } from 'react-redux';
import { setUserDetails } from '../store/userSlice';
import SummaryApi from '../common';
import { useState } from 'react';
import AdminPanel from '../pages/AdminPanel';


function Navbar() { 
  const user = useSelector(state => state?.user?.user)
  console.log('User from Redux state:', user);
  const dispatch = useDispatch()

  const navigate = useNavigate()
  const [menuDisplay,setMenuDisplay] = useState(false)

  const handleLogout = async() => {
    const fetchData = await fetch(SummaryApi.logout_user.url,{
      method : SummaryApi.logout_user.method,
      credentials : 'include'
    })

    const data = await fetchData.json()

    if(data.success){
      alert(data.message)
      dispatch(setUserDetails(null))
      navigate("/")
    }

    if(data.error){
      alert(data.message)
    }

  }

  return (
    <>
      {/* Top Navbar */}
      <nav className="bg-black ">
        <div className="container d-flex justify-content-end">
          <ul className="nav">
            <li className="nav-item "><Link className="nav-link text-white px-3" to="/TataCliqLuxury">Tata CLiQ Luxury</Link></li>
            <li className="nav-item"><Link className="nav-link text-white px-3" to="/CliqCash">CLiQ Cash</Link></li>
            <li className="nav-item"><Link className="nav-link text-white px-3" to="/GiftCard">Gift Card</Link></li>
            <li className="nav-item"><Link className="nav-link text-white px-3" to="/Cliqcare">CLiQ Care</Link></li>
            <li className="nav-item"><Link className="nav-link text-white px-3" to="/Trackorders">Track Orders</Link></li>
            <div className="flex items-center ms-3">
  {
    user?._id ? (
      <button 
        onClick={handleLogout}
        className="bg-[#d00000] hover:bg-[#a30000] text-white px-4 py-1 rounded-full text-sm transition"
      >
        Logout
      </button>
    ) : (
      <Link
        to="/SignIn"
        className="bg-[#d00000] hover:bg-[#a30000] text-white px-4 py-1 rounded-full text-sm transition"
      >
        Login
      </Link>
    )
  }
</div>


            
      </ul>
      </div>
      </nav>

      {/* Main Navbar */}
      <nav className="bg-black ">
        <div className="container d-flex align-items-center justify-content-between">
          {/* Logo */}
          <Link to="/">
            <img
              src="https://distributedrewards-production.s3.amazonaws.com/uploads/gift_card_logo/3279/8e3791e6-7570-42f1-a21f-6b5984ebf3c8.png"
              alt="Tata CLiQ Fashion Logo"
              width="90"
             
            />
          </Link>

          {/* Dropdowns and Search */}
          <div className="d-flex align-items-center flex-grow-1 justify-content-center gap-5">
            {/* Categories Dropdown */}
            <div className="dropdown">
              <button className="btn text-white fw-bold dropdown-toggle" type="button" data-bs-toggle="dropdown">
                Categories
              </button>
              <ul className="dropdown-menu">
                <li><Link className="dropdown-item" to="/Categories/electronics">Electronics</Link></li>
                <li><Link className="dropdown-item" to="/Categories/fashion">Fashion</Link></li>
                <li><Link className="dropdown-item" to="/Categories/home">Home</Link></li>
              </ul>
            </div>

            {/* Brands Dropdown */}
            <div className="dropdown">
              <button className="btn text-white fw-bold dropdown-toggle" type="button" data-bs-toggle="dropdown">
                Brands
              </button>
              <ul className="dropdown-menu">
                <li><Link className="dropdown-item" to="/Brands/nike">Nike</Link></li>
                <li><Link className="dropdown-item" to="/Brands/adidas">Adidas</Link></li>
                <li><Link className="dropdown-item" to="/Brands/apple">Apple</Link></li>
              </ul>
            </div>

            {/* Search Bar */}
            <div className="input-group w-50">
              <span className="input-group-text bg-secondary border-0 text-white">
                <FontAwesomeIcon icon={faSearch} />
              </span>
              <input
                type="text"
                className="form-control bg-secondary border-1 text-white"
                placeholder="Search for Brands"
                aria-label="Search"
              />
            </div>
          </div>

          {/* Icons */}
          <div className="d-flex gap-12">
          <div className='relative flex justify-center'>

                  {
                    user?._id && (
                      <div className='text-3xl cursor-pointer relative flex justify-center' onClick={()=>setMenuDisplay(preve => !preve)}>
                        {
                          user?.profilePic ? (
                            <img src={user?.profilePic} className='w-10 h-10 rounded-full' alt={user?.name} />
                          ) : (
                            <FontAwesomeIcon icon={faCircleUser} />
                          )
                        }
                      </div>
                    )
                  }
                  
                  
                  {
                    menuDisplay && (
                      <div className='absolute bg-white bottom-0 top-11 h-fit p-2 shadow-lg rounded' >
                        <nav>
                          {
                            
                              <Link to={AdminPanel} className='whitespace-nowrap hidden md:block hover:bg-slate-100 p-2 text-black'>AdminPanel</Link>
                            
                          }
                         
                        </nav>
                      </div>
                    )
                  }
                 
                </div>
            <Link className="text-white fs-5" to="/Wishlist" aria-label="Wishlist">
              <FontAwesomeIcon icon={faHeart} />
            </Link>
            <Link className="text-white fs-5" to="/Cart" aria-label="Cart">
              <FontAwesomeIcon icon={faShoppingCart} />
            </Link>
          </div>
        </div>
      </nav>

      <Outlet />
    </>
  );
}

export default Navbar;
