import React, { useEffect, useState } from 'react'
import SummaryApi from '../common'
import moment from 'moment'
import displayINRCurrency from '../helpers/displayCurrency'

const statusSteps = [
  "Pending",
  "Processing",
  "Shipped",
  "Delivered"
]

const OrderPage = () => {
  const [data, setData] = useState([])

  const fetchOrderDetails = async () => {
    const response = await fetch(SummaryApi.getOrder.url, {
      method: SummaryApi.getOrder.method,
      credentials: 'include'
    })

    const responseData = await response.json()
    setData(responseData.data)
    console.log("order list", responseData)
  }

  useEffect(() => {
    fetchOrderDetails()
  }, [])

  // Helper to get the current status index
  const getStatusIndex = (status) => statusSteps.indexOf(status)

  return (
    <div>
      {
        !data[0] && (
          <p>No Order available</p>
        )
      }

      <div className='p-4 w-full'>
        {
          data.map((item, index) => {
            const currentStatusIndex = getStatusIndex(item.status)
            return (
              <div key={item.userId + index} className='mb-8'>
                <p className='font-medium text-lg '>{moment(item.createdAt).format('LL')}</p>
                <div className='border rounded'>
                  <div className='flex flex-col lg:flex-row justify-between'>
                    <div className='grid gap-1'>
                      {
                        item?.productDetails.map((product, index) => {
                          return (
                            <div key={product.productId + index} className='flex  gap-3 bg-slate-100'>
                              <img
                                src={product.image[0]}
                                className='w-28 h-28 bg-slate-200 object-scale-down p-2'
                              />
                              <div>
                                <div className='font-medium text-lg text-ellipsis line-clamp-1'>{product.name}</div>
                                <div className='flex items-center gap-5 mt-1'>
                                  <div className='text-lg text-red-500'>{displayINRCurrency(product.price)}</div>
                                  <p>Quantity : {product.quantity}</p>
                                </div>
                              </div>
                            </div>
                          )
                        })
                      }
                    </div>
                    <div className='flex flex-col gap-4 p-2 min-w-[300px]'>
                      <div>
                        <div className='text-lg font-medium'>Payment Details : </div>
                        <p className=' ml-1'>Payment method : {item.paymentDetails.payment_method_type[0]}</p>
                        <p className=' ml-1'>Payment Status : {item.paymentDetails.payment_status}</p>
                      </div>
                      <div>
                        <div className='text-lg font-medium'>Shipping Details :</div>
                        
                        {/* Shipping Address */}
                        {item.shippingAddress && (
                          <div className='ml-1 mt-2 bg-slate-50 p-2 rounded'>
                            <div className='font-medium'> Address:</div>
                            <div>{item.shippingAddress.name}</div>
                            <div>{item.shippingAddress.addressLine1}</div>
                            {item.shippingAddress.addressLine2 && <div>{item.shippingAddress.addressLine2}</div>}
                            <div>
                              {item.shippingAddress.city}, {item.shippingAddress.state} - {item.shippingAddress.postalCode}
                            </div>
                            <div>{item.shippingAddress.country}</div>
                            <div>Phone: {item.shippingAddress.phone}</div>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>

                  <div className='font-semibold ml-auto w-fit lg:text-lg'>
                    Total Amount : {item.totalAmount}
                  </div>

                  {/* --- Track Your Order Section --- */}
                  <div className='mt-6 p-4 bg-blue-50 rounded'>
                    <h3 className='font-semibold text-base mb-2 text-blue-900'>Track Your Order</h3>
                    <div className='flex items-center gap-2'>
                      {statusSteps.map((step, idx) => (
                        <React.Fragment key={step}>
                          <div className={`flex flex-col items-center`}>
                            <div
                              className={`w-7 h-7 flex items-center justify-center rounded-full
                                ${idx <= currentStatusIndex ? "bg-blue-600 text-white" : "bg-gray-300 text-gray-600"}
                              `}
                            >
                              {idx + 1}
                            </div>
                            <span className={`text-xs mt-1 ${idx <= currentStatusIndex ? "text-blue-700 font-semibold" : "text-gray-500"}`}>
                              {step}
                            </span>
                          </div>
                          {idx < statusSteps.length - 1 && (
                            <div
                              className={`flex-1 h-1 mx-1 rounded
                                ${idx < currentStatusIndex ? "bg-blue-600" : "bg-gray-300"}
                              `}
                              style={{ minWidth: 24 }}
                            ></div>
                          )}
                        </React.Fragment>
                      ))}
                    </div>
                    <div className='mt-2 text-sm text-blue-800'>
                      Current Status: <span className='font-bold'>{item.status}</span>
                    </div>
                  </div>
                  {/* --- End Track Your Order Section --- */}
                </div>
              </div>
            )
          })
        }
      </div>
    </div>
  )
}

export default OrderPage
