// TataCliqCard.jsx
import React from "react";

const TataCliqCard = () => {
  return (
    <div className="w-64 rounded-2xl overflow-hidden shadow-lg bg-white">
      <img
        src="/path/to/26ec64fa-b691-42d9-b101-2824b1f2ddad.png" // replace this with your actual image path
        alt="Tata Cliq Offer"
        className="w-full h-64 object-cover"
      />
      <div className="p-4 text-center">
        <h2 className="text-lg font-semibold text-gray-800">Tata CLiQ</h2>
        <p className="text-sm text-gray-600 mt-2">Amazing Offers Available</p>
        <button className="mt-4 px-4 py-2 bg-black text-white rounded-full hover:bg-gray-800 transition">
          Shop Now
        </button>
      </div>
    </div>
  );
};

export default TataCliqCard;
