import React, { useRef } from "react";
import { FaChevronLeft, FaChevronRight } from "react-icons/fa";

const IndianwearCards = () => {
  const scrollRef = useRef(null);

  const scroll = (direction) => {
    if (scrollRef.current) {
      const scrollAmount = 300;
      scrollRef.current.scrollBy({
        left: direction === "left" ? -scrollAmount : scrollAmount,
        behavior: "smooth",
      });
    }
  };

  const offers = [
    {
      title: "EMBROIDERED KURTAS",
      subtitle: "UP TO 70% OFF",
      image: "/images/kurta.png",
    },
    {
      title: "SUIT SETS",
      subtitle: "UP TO 70% OFF",
      image: "/images/suits.png",
    },
    {
      title: "EMBELLISHED SAREES",
      subtitle: "UP TO 70% OFF",
      image: "/images/saree.png",
    },
    {
      title: "LEHENGAS",
      subtitle: "UP TO 70% OFF",
      image: "/images/lehenga.png",
    },
    // Add more items if needed
  ];

  return (
    <section className="bg-[#f4f4f4] py-10 px-4">
      <div className="relative max-w-screen-xl mx-auto">
        {/* Scrollable Row */}
        <div
          ref={scrollRef}
          className="flex gap-4 overflow-x-auto scroll-smooth hide-scrollbar"
        >
          {offers.map((offer, idx) => (
            <div
              key={idx}
              className="min-w-[220px] max-w-[240px] rounded-3xl overflow-hidden relative group flex-shrink-0"
            >
              <img
                src={offer.image}
                alt={offer.title}
                className="w-full h-[320px] object-cover"
              />
              <div className="absolute bottom-0 w-full bg-gradient-to-t from-black/70 to-transparent px-4 py-3 text-center">
                <h3 className="text-white font-semibold text-sm">{offer.title}</h3>
                <p className="text-white text-xs">{offer.subtitle}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Navigation Arrows */}
        <div className="absolute right-0 top-1/2 -translate-y-1/2 flex gap-2 pr-2">
          <button
            onClick={() => scroll("left")}
            className="bg-white rounded-full p-2 shadow hover:bg-gray-100"
          >
            <FaChevronLeft size={18} />
          </button>
          <button
            onClick={() => scroll("right")}
            className="bg-white rounded-full p-2 shadow hover:bg-gray-100"
          >
            <FaChevronRight size={18} />
          </button>
        </div>
      </div>
    </section>
  );
};

//export default ScrollableOffers;



export default IndianwearCards;
