import React, { useContext } from 'react'
import scrollTop from '../helpers/scrollTop'
import displayINRCurrency from '../helpers/displayCurrency'
import Context from '../context'
import addToCart from '../helpers/addToCart'
import { Link } from 'react-router-dom'

const VerticalCard = ({loading,data = []}) => {
    const loadingList = new Array(13).fill(null)
    const { fetchUserAddToCart } = useContext(Context)

    const handleAddToCart = async(e,id)=>{
       await addToCart(e,id)
       fetchUserAddToCart()
    }

  return (
    <div className='grid grid-cols-[repeat(auto-fit,minmax(260px,300px))] justify-center md:justify-between md:gap-4 overflow-x-scroll scrollbar-none transition-all'>
    {

         loading ? (
             loadingList.map((product,index)=>{
                 return(
                     <div className='w-full min-w-[280px]  md:min-w-[320px] max-w-[280px] md:max-w-[320px]  bg-white rounded-sm shadow '>
                         <div className='bg-slate-200 h-48 p-4 min-w-[280px] md:min-w-[145px] flex justify-center items-center animate-pulse'>
                         </div>
                         <div className='p-4 grid gap-3'>
                             <h2 className='font-medium text-base md:text-lg text-ellipsis line-clamp-1 text-black p-1 py-2 animate-pulse rounded-full bg-slate-200'></h2>
                             <p className='capitalize text-slate-500 p-1 animate-pulse rounded-full bg-slate-200  py-2'></p>
                             <div className='flex gap-3'>
                                 <p className='text-red-600 font-medium p-1 animate-pulse rounded-full bg-slate-200 w-full  py-2'></p>
                                 <p className='text-slate-500 line-through p-1 animate-pulse rounded-full bg-slate-200 w-full  py-2'></p>
                             </div>
                             <button className='text-sm  text-white px-3  rounded-full bg-slate-200  py-2 animate-pulse'></button>
                         </div>
                     </div>
                 )
             })
         ) : (
             data.map((product,index)=>{
                 return(
                <Link
                to={`/product/${product?._id}`}
                key={product._id}
                className="w-full min-w-[240px] max-w-[260px] bg-white shadow-sm border hover:shadow-lg transition-all duration-300 rounded-md overflow-hidden group"
                onClick={scrollTop}
                >
                <div className="h-60 bg-white flex items-center justify-center overflow-hidden relative">
                    <img
                    src={product?.productImage[0]}
                    alt={product?.productName}
                    className="h-full w-full object-contain p-4 transition-transform duration-300 group-hover:scale-105"
                    />
                </div>

                <div className="px-4 py-3 grid gap-1">
                    <h2 className="text-sm md:text-base font-semibold text-gray-900 line-clamp-2">{product?.productName}</h2>
                    <p className="text-xs text-gray-500 uppercase tracking-widest">{product?.category}</p>

                    <div className="flex items-center gap-2 mt-1">
                    <p className="text-base font-bold text-red-600">{displayINRCurrency(product?.sellingPrice)}</p>
                    <p className="text-sm line-through text-gray-400">{displayINRCurrency(product?.price)}</p>
                    </div>

                    <button
                    className="mt-3 w-full text-xs text-center bg-black text-white py-2 rounded-sm hover:bg-red-600 transition-all"
                    onClick={(e) => handleAddToCart(e, product?._id)}
                    >
                    Add to Cart
                    </button>
                </div>
                </Link>

                 )
             })
         )
         
     }
    </div>
  )
}

export default VerticalCard
