import React from 'react'
import { MdDelete } from "react-icons/md";

const Wishlist = ({ data, onDelete, onAddToCart, selectedCategory, setSelectedCategory }) => {
  // Example categories
  const categories = ["All", "T-Shirts"];

  return (
    <div className="min-h-screen bg-white px-4 py-6">
      {/* Heading */}
      <h1 className="text-3xl font-bold mb-6">My Wishlist</h1>
      
      {/* Category Filter */}
      <div className="flex gap-3 mb-8">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`px-5 py-2 rounded font-medium border transition ${
              selectedCategory === cat
                ? "bg-black text-white"
                : "bg-white text-black border-gray-300"
            }`}
            onClick={() => setSelectedCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>
      
      {/* Wishlist Items */}
      <div className="flex flex-wrap gap-8">
        {data.map((product) => (
          <div
            key={product._id}
            className="w-72 bg-white rounded-lg shadow-lg flex flex-col items-center pb-4 relative"
          >
            {/* Product Image with Delete Icon */}
            <div className="relative w-full flex justify-center">
              <img
                src={product?.productId?.productImage?.[0]}
                alt={product?.productId?.productName}
                className="rounded-t-lg w-64 h-80 object-cover"
              />
              <button
                className="absolute top-3 right-3 bg-white bg-opacity-80 rounded-full p-2 shadow hover:bg-red-100"
                onClick={() => onDelete(product._id)}
                aria-label="Remove from wishlist"
              >
                <MdDelete size={22} className="text-gray-600 hover:text-red-600" />
              </button>
            </div>
            {/* Add to Bag Button */}
            <button
              className="w-11/12 mt-4 mb-2 py-2 bg-pink-600 hover:bg-pink-700 text-white font-bold rounded"
              onClick={() => onAddToCart(product?.productId?._id)}
            >
              Add to Bag
            </button>
            {/* Product Info */}
            <div className="w-11/12 text-left">
              <div className="font-bold text-base mb-1">
                {product?.productId?.brandName || "Brand"}
              </div>
              <div className="text-gray-700 text-sm mb-2 line-clamp-2">
                {product?.productId?.productName}
              </div>
              <div className="flex items-baseline gap-2">
                <span className="font-semibold text-lg">
                  ₹{product?.productId?.sellingPrice}
                </span>
                <span className="text-gray-400 line-through text-base">
                  ₹{product?.productId?.price}
                </span>
                <span className="text-green-600 text-sm font-medium">
                  {product?.productId?.price && product?.productId?.sellingPrice
                    ? `${Math.round(
                        ((product?.productId?.price - product?.productId?.sellingPrice) /
                          product?.productId?.price) *
                          100
                      )}% Off`
                    : ""}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Wishlist;
