import React, { useEffect, useState } from 'react'
import SummaryApi from '../common'
import moment from 'moment'
import displayINRCurrency from '../helpers/displayCurrency'
import { FaShoppingCart, FaCog, FaTruck, FaCheckCircle } from 'react-icons/fa'

const statusSteps = [
  { label: "ordered", icon: FaShoppingCart },
  { label: "Processing", icon: FaCog },
  { label: "dispatched", icon: FaTruck },
  { label: "Delivered", icon: FaCheckCircle }
]

const OrderPage = () => {
  const [data, setData] = useState([])

  const fetchOrderDetails = async () => {
    const response = await fetch(SummaryApi.getOrder.url, {
      method: SummaryApi.getOrder.method,
      credentials: 'include'
    })

    const responseData = await response.json()
    setData(responseData.data)
    console.log("order list", responseData)
  }

  useEffect(() => {
    fetchOrderDetails()
  }, [])

  const getStatusIndex = (status) => statusSteps.findIndex(step => step.label.toLowerCase() === status.toLowerCase())

  return (
    <div>
      {
        !data[0] && (
          <p className="text-center mt-10 text-gray-500">No Order available</p>
        )
      }

      <div className="p-4 w-full">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {data.map((item, index) => {
            const currentStatusIndex = getStatusIndex(item.status)
            return (
              <div
                key={item.userId + index}
                className="border rounded shadow bg-white flex flex-col h-full"
              >
                {/* Order ID and Date */}
                <div className="p-4 border-b">
                  <div className="flex flex-col gap-1">
                    <span className="text-xs text-gray-500">
                      Order ID: <span className="font-semibold text-gray-700">{item._id}</span>
                    </span>
                    <span className="font-medium text-lg">{moment(item.createdAt).format('LL')}</span>
                  </div>
                </div>

                <div className="flex flex-col gap-4 p-4 flex-1">
                  {/* Product List */}
                  <div className="grid gap-1">
                    {item?.productDetails.map((product, idx) => (
                      <div key={product.productId + idx} className="flex gap-3 bg-slate-100 mb-2 rounded p-2">
                        <div className="flex gap-2">
                          {product.image.map((imgUrl, i) => (
                            <img
                              key={imgUrl + i}
                              src={imgUrl}
                              className="w-16 h-16 bg-slate-200 object-scale-down p-1 rounded"
                              alt={product.name + ' image ' + (i + 1)}
                            />
                          ))}
                        </div>
                        <div>
                          <div className="font-medium text-base text-ellipsis line-clamp-1">{product.name}</div>
                          <div className="flex items-center gap-5 mt-1">
                            <div className="text-base text-red-500">{displayINRCurrency(product.price)}</div>
                            <p>Qty: {product.quantity}</p>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Track Your Order */}
                  <div className="mt-2 p-3 bg-blue-50 rounded">
                    <h3 className="font-semibold text-base mb-2 text-blue-900">Track Your Order</h3>
                    <div className="flex items-center gap-2">
                      {statusSteps.map((step, idx) => {
                        const Icon = step.icon
                        const isActive = idx === currentStatusIndex
                        const isCompleted = idx < currentStatusIndex

                        return (
                          <React.Fragment key={step.label}>
                            <div className="flex flex-col items-center">
                              <div
                                className={`w-9 h-9 flex items-center justify-center rounded-full border-2
                                  ${isCompleted || isActive
                                    ? "bg-green-600 text-white border-green-600"
                                    : "bg-gray-300 text-gray-600 border-gray-300"}
                                  transition-colors duration-300`}
                              >
                                <Icon size={18} />
                              </div>
                              <span className={`text-xs mt-1 text-center w-20 
                                ${isCompleted || isActive ? "text-green-700 font-semibold" : "text-gray-500"}`}>
                                {step.label}
                              </span>
                            </div>

                            {idx < statusSteps.length - 1 && (
                              <div
                                className={`flex-1 h-1 mx-1 rounded transition-colors duration-300
                                  ${idx < currentStatusIndex ? "bg-green-600" : "bg-gray-300"}`}
                                style={{ minWidth: 24 }}
                              ></div>
                            )}
                          </React.Fragment>
                        )
                      })}
                    </div>
                    <div className="mt-2 text-sm text-blue-800">
                      Current Status: <span className="font-bold">{item.status}</span>
                    </div>
                  </div>

                  {/* Shipping Details */}
                  <div>
                    <div className="text-base font-medium">Shipping Details:</div>
                    {item.shippingAddress && (
                      <div className="ml-1 mt-2 bg-slate-50 p-2 rounded">
                        <div className="font-medium">Address:</div>
                        <div>{item.shippingAddress.name}</div>
                        <div>{item.shippingAddress.addressLine1}</div>
                        {item.shippingAddress.addressLine2 && <div>{item.shippingAddress.addressLine2}</div>}
                        <div>
                          {item.shippingAddress.city}, {item.shippingAddress.state} - {item.shippingAddress.postalCode}
                        </div>
                        <div>{item.shippingAddress.country}</div>
                        <div>Phone: {item.shippingAddress.phone}</div>
                      </div>
                    )}
                  </div>

                  {/* Total Amount */}
                  <div className="font-semibold lg:text-lg mt-2">
                    Total Amount: {displayINRCurrency(item.totalAmount)}
                  </div>
                </div>
              </div>
            )
          })}
        </div>
      </div>
    </div>
  )
}

export default OrderPage
