import React, { useState } from 'react';

function Banner() {
  const [showContent, setShowContent] = useState(false);

  return (
    <div
      className=""
      style={{
        backgroundImage: "url('https://www.icicibank.com/content/dam/icicibank/india/managed-assets/images/offer-zone/ib-tatacliq-offer-d.webp')",
      }}
      onClick={() => setShowContent(true)}
    >
      {/* Hidden content, shown on click */}
      {showContent && (
        <div className="">
          <h2 className="text-3xl font-bold mb-4">Hello World</h2>
          <p className="text-lg">You clicked the banner to reveal this content.</p>
        </div>
      )}
    </div>
  );
}

export default Banner;
