      <div className="flex justify-center gap-6 flex-wrap px-4">
        {/* ICICI Banner */}
        <div className="bg-white border rounded-lg shadow-sm px-6 py-4 flex items-center gap-4 w-full sm:w-[22rem]">
          <img
            src="https://assets.tatacliq.com/medias/sys_master/images/64684496584734.jpg"
            alt="ICICI Bank"
            className="w-20"
          />
          
        </div>

        {/* Paytm Banner */}
        <div className="bg-white border rounded-lg shadow-sm px-6 py-4 flex items-center gap-4 w-full sm:w-[22rem]">
          <img
            src="https://assets.tatacliq.com/medias/sys_master/images/paytm-logo.png"
            alt="Paytm"
            className="w-20"
          />
          <div>
            <p className="font-semibold text-sm sm:text-base">CASHBACK OF UP TO â‚¹200*</p>
            <p className="text-xs text-gray-500">*T&C apply</p>
          </div>
        </div>
      </div>

      {/* Title */}
      <h2 className="text-2xl font-semibold text-center mt-10">Best Brands On Offer</h2>
    </div>
  );
}

export default OfferBanners;
