import React, { useContext, useEffect, useState } from 'react'
import SummaryApi from '../common'
import Context from '../context'
import displayINRCurrency from '../helpers/displayCurrency'
import { MdDelete } from "react-icons/md";
import { loadStripe } from '@stripe/stripe-js';

const Cart = () => {
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const context = useContext(Context)
    const loadingCart = new Array(4).fill(null)

    const fetchData = async () => {
        const response = await fetch(SummaryApi.addToCartProductView.url, {
            method: SummaryApi.addToCartProductView.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
        })
        const responseData = await response.json()
        if (responseData.success) {
            setData(responseData.data)
        }
    }

    const handleLoading = async () => {
        await fetchData()
    }

    useEffect(() => {
        setLoading(true)
        handleLoading().finally(() => setLoading(false)) // CHANGE: Ensure loading is set to false after fetch
    }, [])

    const increaseQty = async (id, qty) => {
        const response = await fetch(SummaryApi.updateCartProduct.url, {
            method: SummaryApi.updateCartProduct.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                _id: id,
                quantity: qty + 1
            })
        })

        const responseData = await response.json()
        if (responseData.success) {
            fetchData()
        }
    }

    const decraseQty = async (id, qty) => {
        if (qty >= 2) {
            const response = await fetch(SummaryApi.updateCartProduct.url, {
                method: SummaryApi.updateCartProduct.method,
                credentials: 'include',
                headers: {
                    "content-type": 'application/json'
                },
                body: JSON.stringify({
                    _id: id,
                    quantity: qty - 1
                })
            })

            const responseData = await response.json()
            if (responseData.success) {
                fetchData()
            }
        }
    }

    const deleteCartProduct = async (id) => {
        const response = await fetch(SummaryApi.deleteCartProduct.url, {
            method: SummaryApi.deleteCartProduct.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                _id: id,
            })
        })

        const responseData = await response.json()
        if (responseData.success) {
            fetchData()
            context.fetchUserAddToCart()
        }
    }

    const handlePayment = async () => {
        // console.log("process.env.REACT_APP_STRIPE_PUBLIC_KEY", process.env.REACT_APP_STRIPE_PUBLIC_KEY);
        const stripePromise = await loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY)
        const response = await fetch(SummaryApi.payment.url, {
            method: SummaryApi.payment.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                cartItems: data
            })
        })
        const responseData = await response.json()
        if (responseData?.id) {
            stripePromise.redirectToCheckout({ sessionId: responseData.id })
        }
        // console.log("payment response", responseData)
    }

    // CHANGE: Use optional chaining for sellingPrice
    const totalQty = data.reduce((previousValue, currentValue) => previousValue + currentValue.quantity, 0)
    const totalPrice = data.reduce((preve, curr) => preve + (curr.quantity * (curr?.productId?.sellingPrice || 0)), 0) // CHANGE

    return (
       <div className="container mx-auto px-4 py-6">
  <h1 className="text-2xl md:text-3xl font-bold mb-6 text-gray-800">My Bag</h1>

  <div className="flex flex-col lg:flex-row gap-6">
    {/* Left Side - Product List */}
    <div className="flex-1 space-y-4">
      {data.length === 0 && !loading && (
        <div className="bg-white text-center py-16 rounded shadow">
          <h2 className="text-xl font-semibold text-gray-800">Your Cart is Empty</h2>
          <p className="text-sm text-gray-500 mt-2">Add items to see them here.</p>
        </div>
      )}

      {data.map((product, index) => (
        <div
          key={index}
          className="flex flex-col md:flex-row bg-white border rounded-lg shadow-sm p-4 gap-4"
        >
          {/* Product Image */}
          <div className="w-full md:w-32 h-32 flex-shrink-0 bg-gray-50 flex items-center justify-center rounded">
            <img
              src={product?.productId?.productImage?.[0]}
              alt="product"
              className="w-full h-full object-contain mix-blend-multiply"
            />
          </div>

          {/* Product Details */}
          <div className="flex-1 relative">
            <button
              onClick={() => deleteCartProduct(product?._id)}
              className="absolute top-0 right-0 text-red-500 hover:text-white hover:bg-red-500 p-1 rounded-full"
            >
              <MdDelete size={20} />
            </button>

            <h3 className="text-lg font-semibold text-gray-800 truncate">
              {product?.productId?.productName}
            </h3>
            <p className="text-sm text-gray-500">{product?.productId?.category}</p>

            <div className="flex justify-between mt-2 text-sm font-medium text-gray-700">
              <span>Price:</span>
              <span>{displayINRCurrency(product?.productId?.sellingPrice)}</span>
            </div>

            <div className="flex justify-between mt-1 text-sm font-semibold text-gray-800">
              <span>Total:</span>
              <span>
                {displayINRCurrency(product?.productId?.sellingPrice * product?.quantity)}
              </span>
            </div>

            <div className="flex items-center gap-2 mt-3">
              <button
                className="w-7 h-7 border border-red-500 text-red-500 hover:bg-red-500 hover:text-white rounded"
                onClick={() => decraseQty(product?._id, product?.quantity)}
              >
                -
              </button>
              <span>{product?.quantity}</span>
              <button
                className="w-7 h-7 border border-red-500 text-red-500 hover:bg-red-500 hover:text-white rounded"
                onClick={() => increaseQty(product?._id, product?.quantity)}
              >
                +
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>

    {/* Right Side - Summary */}
    {data.length > 0 && (
      <div className="w-full lg:w-96 space-y-4">
        {/* Deliver To Section */}
        <div className="bg-white border rounded shadow-sm p-4">
          <div className="flex justify-between items-center">
            <p className="font-semibold text-gray-800">Deliver To</p>
            <button className="text-pink-600 text-sm font-medium">Change</button>
          </div>
          <p className="mt-1 text-sm text-gray-600">110001, Delhi</p>
        </div>

        {/* Coupons */}
        <div className="bg-white border rounded shadow-sm p-4 cursor-pointer flex justify-between items-center">
          <span className="text-sm font-medium text-gray-800">Check for Coupons</span>
          <span className="text-gray-500">âž”</span>
        </div>

        {/* Summary Box */}
        <div className="bg-white border rounded shadow overflow-hidden">
          <div className="bg-red-600 text-white text-lg font-semibold p-4">
            Summary
          </div>
          <div className="p-4 space-y-3 text-sm font-medium text-gray-800">
            <div className="flex justify-between">
              <span>Quantity</span>
              <span>{totalQty}</span>
            </div>
            <div className="flex justify-between">
              <span>Total Price</span>
              <span>{displayINRCurrency(totalPrice)}</span>
            </div>
            <button
              onClick={handlePayment}
              className="w-full mt-4 bg-pink-600 hover:bg-pink-700 text-white font-semibold py-2 rounded"
            >
              Checkout
            </button>
          </div>
        </div>
      </div>
    )}
  </div>
</div>
    )
}

export default Cart
