import React, { useContext, useEffect, useState } from 'react';
import fetchCategoryWiseProduct from '../helpers/fetchCategoryWiseProduct';
import displayINRCurrency from '../helpers/displayCurrency';
import { Link } from 'react-router-dom';
import { FaHeart, FaStar, FaShoppingCart, FaPlus } from 'react-icons/fa';
import addToCart from '../helpers/addToCart';
import Context from '../context';

const HorizontalCardProduct = ({ category, heading }) => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const { fetchUserAddToCart } = useContext(Context);

  const fetchData = async () => {
    setLoading(true);
    const categoryProduct = await fetchCategoryWiseProduct(category);
    setData(categoryProduct?.data || []);
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleAddToCart = async (e, id) => {
    e.preventDefault();
    await addToCart(e, id);
    fetchUserAddToCart();
  };

  const handleWishlist = (e, id) => {
    e.preventDefault();
    // Implement your wishlist logic here (local storage, context, or backend)
    console.log(`Added to wishlist: ${id}`);
  };

  const loadingCards = new Array(4).fill(null);

  return (
    <div className="container mx-auto px-4 my-6">
      <h2 className="text-2xl font-semibold py-4">{heading}</h2>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {(loading ? loadingCards : data).map((product, index) =>
          loading ? (
            <div
              key={index}
              className="bg-white rounded-md shadow p-4 animate-pulse h-[360px]"
            >
              <div className="h-32 bg-slate-200 rounded"></div>
              <div className="mt-3 h-4 bg-slate-200 rounded w-3/4"></div>
              <div className="mt-2 h-3 bg-slate-200 rounded w-1/2"></div>
              <div className="mt-2 flex gap-2">
                <div className="h-3 bg-slate-200 rounded w-1/3"></div>
                <div className="h-3 bg-slate-200 rounded w-1/3"></div>
              </div>
            </div>
          ) : (
            <Link
              to={`/product/${product._id}`}
              key={product._id}
              className="bg-white rounded-md shadow p-4 relative group hover:shadow-lg transition"
            >
              {/* Wishlist Button */}
              <button
                onClick={(e) => handleWishlist(e, product._id)}
                className="absolute top-2 right-2 text-gray-500 hover:text-red-500 text-lg"
              >
                <FaHeart />
              </button>

              {/* Product Image */}
              <div className="flex items-center justify-center h-32">
                <img
                  src={product.productImage[0]}
                  alt={product.productName}
                  className="max-h-full object-contain"
                />
              </div>

              {/* Dot indicators (mocked as static) */}
              <div className="flex justify-center mt-1 space-x-1">
                <span className="h-1.5 w-1.5 bg-gray-400 rounded-full"></span>
                <span className="h-1.5 w-1.5 bg-gray-400 rounded-full"></span>
                <span className="h-1.5 w-1.5 bg-gray-400 rounded-full"></span>
              </div>

              {/* Product Info */}
              <h3 className="mt-3 font-semibold text-sm">{product.brand || 'Brand'}</h3>
              <p className="text-sm text-gray-600 line-clamp-2">{product.productName}</p>

              {/* Price Info */}
              <div className="mt-1 text-sm flex items-center gap-2">
                <span className="font-semibold text-gray-900">
                  {displayINRCurrency(product.sellingPrice)}
                </span>
                <span className="line-through text-gray-400 text-xs">
                  {displayINRCurrency(product.price)}
                </span>
                <span className="text-green-600 text-xs">
                  {Math.round(
                    100 - (product.sellingPrice / product.price) * 100
                  )}
                  % off
                </span>
              </div>

              

             

              {/* Add to Cart Button */}
              <button
                onClick={(e) => handleAddToCart(e, product._id)}
                className="mt-2 w-full bg-red-600 text-white py-1 rounded hover:bg-red-700 text-sm flex items-center justify-center gap-2"
              >
                <FaPlus /> Add to Cart
              </button>
            </Link>
          )
        )}
      </div>
    </div>
  );
};

export default HorizontalCardProduct;
