import React, { useContext, useEffect, useState } from 'react'
import SummaryApi from '../common'
import Context from '../context'
import displayINRCurrency from '../helpers/displayCurrency'
import { MdDelete } from "react-icons/md";
import { loadStripe } from '@stripe/stripe-js';

const Cart = () => {
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const context = useContext(Context)
    const loadingCart = new Array(4).fill(null)

    const fetchData = async () => {
        const response = await fetch(SummaryApi.addToCartProductView.url, {
            method: SummaryApi.addToCartProductView.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
        })
        const responseData = await response.json()
        if (responseData.success) {
            setData(responseData.data)
        }
    }

    const handleLoading = async () => {
        await fetchData()
    }

    useEffect(() => {
        setLoading(true)
        handleLoading().finally(() => setLoading(false))
    }, [])

    const increaseQty = async (id, qty) => {
        const response = await fetch(SummaryApi.updateCartProduct.url, {
            method: SummaryApi.updateCartProduct.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                _id: id,
                quantity: qty + 1
            })
        })

        const responseData = await response.json()
        if (responseData.success) {
            fetchData()
        }
    }

    const decraseQty = async (id, qty) => {
        if (qty >= 2) {
            const response = await fetch(SummaryApi.updateCartProduct.url, {
                method: SummaryApi.updateCartProduct.method,
                credentials: 'include',
                headers: {
                    "content-type": 'application/json'
                },
                body: JSON.stringify({
                    _id: id,
                    quantity: qty - 1
                })
            })

            const responseData = await response.json()
            if (responseData.success) {
                fetchData()
            }
        }
    }

    const deleteCartProduct = async (id) => {
        const response = await fetch(SummaryApi.deleteCartProduct.url, {
            method: SummaryApi.deleteCartProduct.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                _id: id,
            })
        })

        const responseData = await response.json()
        if (responseData.success) {
            fetchData()
            context.fetchUserAddToCart()
        }
    }

    const handlePayment = async () => {
        const stripePromise = await loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY)
        const response = await fetch(SummaryApi.payment.url, {
            method: SummaryApi.payment.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                cartItems: data
            })
        })
        const responseData = await response.json()
        if (responseData?.id) {
            stripePromise.redirectToCheckout({ sessionId: responseData.id })
        }
    }

    const totalQty = data.reduce((previousValue, currentValue) => previousValue + currentValue.quantity, 0)
    const totalPrice = data.reduce((preve, curr) => preve + (curr.quantity * (curr?.productId?.sellingPrice || 0)), 0)

    // Example static values for summary, replace with your logic if needed
    const bagTotal = 1799.00
    const processingFee = 29.00
    const processingFeeOriginal = 99.00
    const bagSubtotal = 1828.00
    const productDiscount = 746.90
    const total = 1081.10

    return (
        <div className="bg-[#fff] min-h-screen">
            {/* Header */}
            <div className="relative bg-[#ffe9f0] py-8 px-4 md:px-12">
                <h1 className="text-3xl font-bold text-gray-800">My Bag</h1>
                <div className="absolute top-0 right-0">
                    <svg width="180" height="90" viewBox="0 0 180 90" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <ellipse cx="140" cy="10" rx="60" ry="40" fill="#ffe9f0" />
                    </svg>
                </div>
            </div>

            {/* Notification */}
            <div className="container mx-auto px-4 mt-4">
                <div className="bg-green-50 border border-green-200 rounded px-4 py-2 text-green-700 flex items-center gap-2 text-sm">
                    <svg width="18" height="18" fill="none" stroke="currentColor" className="text-green-500"><circle cx="9" cy="9" r="8" strokeWidth="2" /><path d="M9 5v4l2 2" strokeWidth="2" strokeLinecap="round" /></svg>
                    Review your cart summary for the final amount and any eligible shipping discounts.
                </div>
            </div>

            {/* Main Content */}
            <div className="container mx-auto px-4 mt-6 flex flex-col lg:flex-row gap-8">
                {/* Left: Cart Items */}
                <div className="flex-1">
                    {loading ? (
                        loadingCart.map((_, idx) => (
                            <div key={idx} className="bg-slate-100 h-36 rounded-lg mb-4 animate-pulse" />
                        ))
                    ) : (
                        data.map((product, index) => {
                            if (!product?.productId) return null
                            return (
                                <div key={product._id} className="bg-white border rounded-xl shadow-sm mb-6 flex flex-col md:flex-row items-center px-6 py-4">
                                    <div className="w-28 h-32 bg-gray-100 rounded-lg overflow-hidden flex-shrink-0">
                                        <img src={product?.productId?.productImage?.[0]} alt={product?.productId?.productName || "Product"} className="w-full h-full object-cover" />
                                    </div>
                                    <div className="flex-1 md:ml-6 mt-4 md:mt-0 w-full">
                                        <div className="flex flex-col md:flex-row md:items-center justify-between">
                                            <div>
                                                <h2 className="text-lg font-semibold text-gray-800">{product?.productId?.productName || "No name"}</h2>
                                                <div className="flex items-center gap-2 mt-1">
                                                    <span className="text-xl font-bold text-gray-900">{displayINRCurrency(product?.productId?.sellingPrice || 0)}</span>
                                                    <span className="line-through text-gray-400 text-base">{displayINRCurrency(product?.productId?.price || 0)}</span>
                                                    <span className="text-green-600 text-base font-medium">
                                                        {product?.productId?.price && product?.productId?.sellingPrice
                                                            ? `₹${(product?.productId?.price - product?.productId?.sellingPrice).toFixed(2)} Off`
                                                            : null}
                                                    </span>
                                                </div>
                                                <p className="text-gray-500 mt-1">Color: {product?.productId?.color || "N/A"}</p>
                                            </div>
                                            <div className="flex items-center gap-2 mt-2 md:mt-0">
                                                <svg width="20" height="20" fill="none" stroke="currentColor" className="text-gray-400"><rect x="2" y="6" width="16" height="8" rx="2" strokeWidth="1.5"/><path d="M2 10h16" strokeWidth="1.5"/></svg>
                                                <span className="text-gray-600 text-sm">Delivery by <span className="font-semibold text-gray-800">20th May</span></span>
                                            </div>
                                        </div>
                                        <div className="flex flex-col md:flex-row gap-4 items-center mt-4">
                                            <div className="flex gap-4">
                                                <div>
                                                    <label className="block text-xs text-gray-500 mb-1">Size</label>
                                                    <select className="border rounded px-2 py-1 text-sm">
                                                        <option>{product?.size || 'N/A'}</option>
                                                    </select>
                                                </div>
                                                <div>
                                                    <label className="block text-xs text-gray-500 mb-1">Qty</label>
                                                    <select
                                                        className="border rounded px-2 py-1 text-sm"
                                                        value={product?.quantity}
                                                        onChange={e => {
                                                            const val = parseInt(e.target.value)
                                                            if (val > product?.quantity) increaseQty(product?._id, product?.quantity)
                                                            else if (val < product?.quantity) decraseQty(product?._id, product?.quantity)
                                                        }}
                                                    >
                                                        {[1,2,3,4,5].map(qty => (
                                                            <option key={qty} value={qty}>{qty}</option>
                                                        ))}
                                                    </select>
                                                </div>
                                            </div>
                                            <div className="flex-1 flex justify-end items-center gap-6 mt-2 md:mt-0">
                                                <button className="flex items-center gap-1 text-gray-600 hover:text-pink-600 text-sm font-medium">
                                                    <svg width="18" height="18" fill="none" stroke="currentColor" className="text-lg"><path d="M9 3v12M3 9h12" strokeWidth="2" strokeLinecap="round"/></svg>
                                                    Save to wishlist
                                                </button>
                                                <button
                                                    className="text-gray-500 hover:text-red-600 text-sm font-medium"
                                                    onClick={() => deleteCartProduct(product?._id)}
                                                >
                                                    Remove
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            )
                        })
                    )}
                </div>

                {/* Right: Summary */}
                <div className="w-full max-w-sm flex flex-col gap-4">
                    {/* Deliver To */}
                    <div className="bg-white rounded-xl border px-6 py-4 flex justify-between items-center">
                        <div>
                            <div className="text-xs text-gray-500">Deliver To</div>
                            <div className="font-semibold text-base text-gray-800">110001, Delhi</div>
                        </div>
                        <button className="text-pink-600 text-sm font-medium hover:underline">Change</button>
                    </div>
                    {/* Coupons */}
                    <div className="bg-white rounded-xl border px-6 py-4 flex items-center justify-between cursor-pointer hover:bg-pink-50">
                        <div className="flex items-center gap-2">
                            <svg width="20" height="20" fill="none" stroke="currentColor" className="text-pink-600"><rect x="3" y="5" width="14" height="10" rx="2" strokeWidth="1.5"/><path d="M7 9h6" strokeWidth="1.5"/></svg>
                            <span className="font-medium text-gray-800">Check for Coupons</span>
                        </div>
                        <svg width="18" height="18" fill="none" stroke="currentColor" className="text-gray-400"><path d="M7 4l6 5-6 5" strokeWidth="2" strokeLinecap="round"/></svg>
                    </div>
                    {/* Price Summary */}
                    <div className="bg-white rounded-xl border px-6 py-6">
                        <div className="flex justify-between mb-2 text-gray-700">
                            <span>Bag Total</span>
                            <span>{displayINRCurrency(bagTotal)}</span>
                        </div>
                        <div className="flex justify-between mb-2 items-center">
                            <span>Processing Fee</span>
                            <div className="flex items-center gap-2">
                                <span className="line-through text-gray-400">{displayINRCurrency(processingFeeOriginal)}</span>
                                <span>{displayINRCurrency(processingFee)}</span>
                            </div>
                        </div>
                        <div className="flex justify-between mb-2 text-gray-700">
                            <span>Bag Subtotal</span>
                            <span>{displayINRCurrency(bagSubtotal)}</span>
                        </div>
                        <div className="flex justify-between mb-2 text-green-600 font-medium">
                            <span>Product Discount(s)</span>
                            <span>-{displayINRCurrency(productDiscount)}</span>
                        </div>
                        <div className="text-green-700 text-sm mb-2">
                            You will save ₹{productDiscount} on this order
                        </div>
                        <div className="flex justify-between font-bold text-lg mt-4">
                            <span>Total</span>
                            <span>{displayINRCurrency(total)}</span>
                        </div>
                        <button
                            className="w-full bg-pink-600 text-white rounded-lg py-3 mt-6 font-semibold text-lg hover:bg-pink-700 transition"
                            onClick={handlePayment}
                        >
                            Checkout
                        </button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Cart
