import React, { useEffect, useState } from 'react'
import SummaryApi from '../common'
import moment from 'moment'
import displayINRCurrency from '../helpers/displayCurrency'
import { FaShoppingCart, FaCog, FaTruck, FaCheckCircle } from 'react-icons/fa'

const statusSteps = [
  { label: "ordered", icon: FaShoppingCart },
  { label: "Processing", icon: FaCog },
  { label: "dispatched", icon: FaTruck },
  { label: "Delivered", icon: FaCheckCircle }
]

const OrderPage = () => {
  const [data, setData] = useState([])
  const [selectedOrder, setSelectedOrder] = useState(null)

  const fetchOrderDetails = async () => {
    const response = await fetch(SummaryApi.getOrder.url, {
      method: SummaryApi.getOrder.method,
      credentials: 'include'
    })

    const responseData = await response.json()
    setData(responseData.data)
  }

  useEffect(() => {
    fetchOrderDetails()
  }, [])

  const getStatusIndex = (status) => statusSteps.findIndex(step => step.label.toLowerCase() === status.toLowerCase())

  // Modal close handler
  const closeModal = () => setSelectedOrder(null)

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {!data.length && (
        <p className="text-center mt-20 text-gray-500">No Orders Available</p>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {data.map(order => {
          const currentStatusIndex = getStatusIndex(order.status)
          const firstProduct = order.productDetails[0]

          return (
            <div
              key={order._id}
              className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300 p-4 flex flex-col justify-between h-60 max-w-md"
            >
              {/* Top: Product + Order Info */}
              <div className="flex gap-4">
                <img
                  src={firstProduct.image[0]}
                  alt={firstProduct.name}
                  className="w-20 h-20 object-contain rounded"
                />
                <div className="flex flex-col justify-between flex-1">
                  <div>
                    <h3 className="font-semibold text-gray-900 line-clamp-2">{firstProduct.name}</h3>
                    <p className="text-red-600 font-semibold mt-1">{displayINRCurrency(firstProduct.price)}</p>
                    <p className="text-gray-600 text-sm mt-0.5">Qty: {firstProduct.quantity}</p>
                  </div>
                  <div className="text-sm text-gray-500 mt-2">
                    Order ID: <span className="font-medium text-gray-700">{order._id}</span>
                  </div>
                </div>
              </div>

              {/* Bottom: Date, Status, Total, Button */}
              <div className="mt-4 flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">{moment(order.createdAt).format('DD MMM, YYYY')}</p>
                  <p
                    className={`inline-block mt-1 px-2 py-1 text-xs font-semibold rounded-full
                      ${
                        order.status.toLowerCase() === "delivered" ? "bg-green-100 text-green-700" :
                        order.status.toLowerCase() === "processing" ? "bg-yellow-100 text-yellow-700" :
                        order.status.toLowerCase() === "dispatched" ? "bg-blue-100 text-blue-700" :
                        "bg-gray-100 text-gray-700"
                      }`}
                  >
                    {order.status}
                  </p>
                </div>
                <div className="text-lg font-semibold text-gray-900">{displayINRCurrency(order.totalAmount)}</div>
                <button
                  onClick={() => setSelectedOrder(order)}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md text-sm font-semibold transition"
                >
                  View Details
                </button>
              </div>
            </div>
          )
        })}
      </div>

      {/* Modal */}
      {selectedOrder && (
        <div
          className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50 p-4"
          onClick={closeModal}
        >
          <div
            className="bg-white rounded-lg max-w-3xl w-full max-h-[80vh] overflow-y-auto p-6 relative"
            onClick={e => e.stopPropagation()}
          >
            <button
              onClick={closeModal}
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-900"
              aria-label="Close modal"
            >
              âœ•
            </button>

            <h2 className="text-2xl font-semibold mb-4">Order Details</h2>

            {/* Products List */}
            <div>
              <h3 className="text-lg font-semibold mb-2">Products</h3>
              {selectedOrder.productDetails.map((product, idx) => (
                <div
                  key={product.productId + idx}
                  className="flex gap-4 items-center bg-gray-100 rounded p-3 mb-3"
                >
                  <img
                    src={product.image[0]}
                    alt={product.name}
                    className="w-20 h-20 object-contain rounded"
                  />
                  <div className="flex flex-col flex-1">
                    <div className="font-semibold text-gray-900 line-clamp-2">{product.name}</div>
                    <div className="text-red-600 font-semibold mt-1">{displayINRCurrency(product.price)}</div>
                    <div className="text-gray-700">Qty: {product.quantity}</div>
                  </div>
                </div>
              ))}
            </div>

            {/* Order Tracking */}
            <div className="mt-6">
              <h3 className="text-lg font-semibold mb-3">Track Your Order</h3>
              <div className="flex items-center gap-4">
                {statusSteps.map((step, idx) => {
                  const Icon = step.icon
                  const currentStatusIndex = getStatusIndex(selectedOrder.status)
                  const isActive = idx === currentStatusIndex
                  const isCompleted = idx < currentStatusIndex

                  return (
                    <div key={step.label} className="flex flex-col items-center">
                      <div
                        className={`w-10 h-10 flex items-center justify-center rounded-full border-2
                          ${isCompleted || isActive
                            ? "bg-green-600 text-white border-green-600"
                            : "bg-gray-300 text-gray-600 border-gray-300"}
                          transition-colors duration-300`}
                      >
                        <Icon size={20} />
                      </div>
                      <span
                        className={`text-xs mt-1 text-center w-20
                          ${isCompleted || isActive ? "text-green-700 font-semibold" : "text-gray-500"}`}
                      >
                        {step.label}
                      </span>
                      {idx < statusSteps.length - 1 && (
                        <div
                          className={`w-10 h-1 mt-2 rounded
                            ${idx < currentStatusIndex ? "bg-green-600" : "bg-gray-300"}`}
                        ></div>
                      )}
                    </div>
                  )
                })}
              </div>
              <p className="mt-2 text-sm text-green-700 font-semibold">
                Current Status: {selectedOrder.status}
              </p>
            </div>

            {/* Shipping Details */}
            <div className="mt-6">
              <h3 className="text-lg font-semibold mb-2">Shipping Details</h3>
              <div className="bg-gray-50 p-4 rounded text-gray-700">
                <p><strong>Name:</strong> {selectedOrder.shippingAddress?.name}</p>
                <p><strong>Address:</strong> {selectedOrder.shippingAddress?.addressLine1}</p>
                {selectedOrder.shippingAddress?.addressLine2 && <p>{selectedOrder.shippingAddress.addressLine2}</p>}
                <p>
                  {selectedOrder.shippingAddress?.city}, {selectedOrder.shippingAddress?.state} - {selectedOrder.shippingAddress?.postalCode}
                </p>
                <p>{selectedOrder.shippingAddress?.country}</p>
                <p><strong>Phone:</strong> {selectedOrder.shippingAddress?.phone}</p>
              </div>
            </div>

            {/* Total Amount */}
            <div className="mt-6 text-right font-semibold text-lg">
              Total Amount: {displayINRCurrency(selectedOrder.totalAmount)}
            </div>
          </div>
        </div>
      )}
    </div>
  )
}

export default OrderPage
