import React, { useEffect, useState } from 'react';
import SummaryApi from '../common';
import moment from 'moment';
import displayINRCurrency from '../helpers/displayCurrency';
import { FaShoppingCart, FaCog, FaTruck, FaCheckCircle } from 'react-icons/fa';

const statusSteps = [
  { label: "ordered", icon: FaShoppingCart },
  { label: "Processing", icon: FaCog },
  { label: "dispatched", icon: FaTruck },
  { label: "Delivered", icon: FaCheckCircle }
];

const getStatusIndex = (status) =>
  statusSteps.findIndex(step => step.label.toLowerCase() === status.toLowerCase());

const getStatusLabelColor = (status) => {
  switch (status.toLowerCase()) {
    case 'ordered':
      return 'bg-gray-400 text-white';
    case 'processing':
      return 'bg-yellow-500 text-white';
    case 'dispatched':
      return 'bg-blue-500 text-white';
    case 'delivered':
      return 'bg-green-600 text-white';
    default:
      return 'bg-gray-300 text-gray-700';
  }
};

const OrderSummaryCard = ({ order, onViewDetails, isExpanded }) => (
  <div className="bg-white rounded-lg shadow-sm border border-gray-200 hover:shadow-md transition-all duration-300 w-full max-w-md mx-auto overflow-hidden">
    <div className="p-4 flex flex-col gap-2">
      <div className="flex justify-between text-sm text-gray-500">
        <span>Order ID: <span className="font-medium text-gray-700">{order._id.slice(-6)}</span></span>
        <span>{moment(order.createdAt).format('DD MMM, YYYY')}</span>
      </div>

      {order.productDetails[0] && (
        <div className="flex items-center gap-4 mt-3">
          <img
            src={order.productDetails[0].image[0]}
            alt={order.productDetails[0].name}
            className="w-16 h-16 object-contain bg-gray-100 p-1 rounded"
          />
          <div className="flex-1">
            <p className="text-sm font-medium text-gray-800 line-clamp-1">{order.productDetails[0].name}</p>
            <p className="text-xs text-gray-500 mt-1">Qty: {order.productDetails[0].quantity}</p>
          </div>
        </div>
      )}

      <div className="flex justify-between items-center mt-4">
        <span className={`text-xs px-2 py-1 rounded-full uppercase font-semibold ${getStatusLabelColor(order.status)}`}>
          {order.status}
        </span>
        <span className="font-semibold text-gray-800 text-sm">{displayINRCurrency(order.totalAmount)}</span>
      </div>

      <button
        className="mt-4 w-full text-center bg-black text-white py-2 rounded text-sm font-medium hover:bg-gray-800"
        onClick={onViewDetails}
      >
        {isExpanded ? "Hide Details" : "View Details"}
      </button>
    </div>
  </div>
);

const OrderDetails = ({ order }) => {
  const currentStatusIndex = getStatusIndex(order.status);

  return (
    <div className="bg-[#fafafa] p-4 rounded-b border-t border-gray-200">
      {/* Product Details */}
      <div className="grid gap-2 mb-4">
        {order.productDetails.map((product, idx) => (
          <div key={product.productId + idx} className="flex gap-3 bg-slate-100 rounded p-3">
            <img
              src={product.image[0]}
              className="w-12 h-12 bg-slate-200 object-contain p-1 rounded"
              alt={product.name}
            />
            <div>
              <div className="font-medium text-sm text-ellipsis line-clamp-1">{product.name}</div>
              <div className="flex items-center gap-4 mt-1 text-xs">
                <div className="text-red-600">{displayINRCurrency(product.price)}</div>
                <div>Qty: {product.quantity}</div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Track Order */}
      <div className="mt-2 p-4 bg-blue-50 rounded">
        <h3 className="font-semibold text-sm mb-2 text-blue-900">Track Your Order</h3>
        <div className="flex items-center gap-2">
          {statusSteps.map((step, idx) => {
            const Icon = step.icon;
            const isActive = idx === currentStatusIndex;
            const isCompleted = idx < currentStatusIndex;
            return (
              <React.Fragment key={step.label}>
                <div className="flex flex-col items-center">
                  <div
                    className={`w-9 h-9 flex items-center justify-center rounded-full border-2
                    ${isCompleted || isActive
                        ? "bg-green-600 text-white border-green-600"
                        : "bg-gray-300 text-gray-600 border-gray-300"}`}
                  >
                    <Icon size={16} />
                  </div>
                  <span className={`text-[10px] mt-1 text-center w-20 ${isCompleted || isActive ? "text-green-700 font-semibold" : "text-gray-500"}`}>
                    {step.label}
                  </span>
                </div>
                {idx < statusSteps.length - 1 && (
                  <div
                    className={`flex-1 h-1 mx-1 rounded ${idx < currentStatusIndex ? "bg-green-600" : "bg-gray-300"}`}
                    style={{ minWidth: 24 }}
                  ></div>
                )}
              </React.Fragment>
            );
          })}
        </div>
        <p className="mt-3 text-xs text-blue-900">
          Current Status: <span className="font-semibold">{order.status}</span>
        </p>
      </div>

      {/* Shipping Details */}
      <div className="mt-4 text-sm">
        <div className="font-semibold mb-1">Shipping Address:</div>
        {order.shippingAddress && (
          <div className="bg-white p-3 rounded border text-gray-700">
            <p>{order.shippingAddress.name}</p>
            <p>{order.shippingAddress.addressLine1}</p>
            {order.shippingAddress.addressLine2 && <p>{order.shippingAddress.addressLine2}</p>}
            <p>{order.shippingAddress.city}, {order.shippingAddress.state} - {order.shippingAddress.postalCode}</p>
            <p>{order.shippingAddress.country}</p>
            <p>Phone: {order.shippingAddress.phone}</p>
          </div>
        )}
      </div>

      <div className="text-right mt-4 text-sm font-semibold text-gray-700">
        Total Amount: {displayINRCurrency(order.totalAmount)}
      </div>
    </div>
  );
};

const OrderPage = () => {
  const [data, setData] = useState([]);
  const [expandedIndex, setExpandedIndex] = useState(null);

  const fetchOrderDetails = async () => {
    const response = await fetch(SummaryApi.getOrder.url, {
      method: SummaryApi.getOrder.method,
      credentials: 'include'
    });
    const responseData = await response.json();
    setData(responseData.data);
  };

  useEffect(() => {
    fetchOrderDetails();
  }, []);

  return (
    <div className="p-4 w-full">
      {!data.length && (
        <p className="text-center mt-10 text-gray-500">No Orders Available</p>
      )}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {data.map((item, index) => (
          <div key={item._id + index}>
            <OrderSummaryCard
              order={item}
              isExpanded={expandedIndex === index}
              onViewDetails={() => setExpandedIndex(expandedIndex === index ? null : index)}
            />
            {expandedIndex === index && (
              <OrderDetails order={item} />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrderPage;
