import React, { useContext, useEffect, useState } from 'react'
import fetchCategoryWiseProduct from '../helpers/fetchCategoryWiseProduct'
import displayINRCurrency from '../helpers/displayCurrency'
import { Link } from 'react-router-dom'
import addToCart from '../helpers/addToCart'
import Context from '../context'
import scrollTop from '../helpers/scrollTop'

const CategoryWiseProductDisplay = ({ category, brand, heading }) => {
  const [data, setData] = useState([])
  const [loading, setLoading] = useState(true)
  const loadingList = new Array(8).fill(null)

  const { fetchUserAddToCart } = useContext(Context)

  const handleAddToCart = async (e, id) => {
    e.preventDefault()
    await addToCart(e, id)
    fetchUserAddToCart()
  }

  const fetchData = async () => {
    setLoading(true)
    const categoryProduct = await fetchCategoryWiseProduct(category, brand)
    setData(categoryProduct?.data || [])
    setLoading(false)
  }

  useEffect(() => {
    fetchData()
  }, [])

  const calculateDiscount = (price, sellingPrice) => {
    return ((price - sellingPrice) / price) * 100
  }

  return (
    <div className="container mx-auto px-4 my-8">
      <h2 className="text-2xl font-semibold mb-6 text-black">{heading}</h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {(loading ? loadingList : data).map((product, index) => {
          if (loading) {
            return (
              <div
                key={index}
                className="bg-white rounded-lg shadow animate-pulse h-[400px] p-4 flex flex-col gap-4"
              >
                <div className="bg-slate-200 h-48 w-full rounded"></div>
                <div className="bg-slate-200 h-4 rounded w-3/4"></div>
                <div className="bg-slate-200 h-3 rounded w-1/2"></div>
                <div className="bg-slate-200 h-4 rounded w-full"></div>
                <div className="bg-slate-200 h-10 rounded w-full mt-auto"></div>
              </div>
            )
          }

          const discount = calculateDiscount(product.price, product.sellingPrice)

          return (
            <Link
              key={product._id}
              to={`/product/${product._id}`}
              onClick={scrollTop}
              className="bg-white rounded-lg shadow hover:shadow-lg transition-all duration-300 flex flex-col overflow-hidden group"
            >
              {/* Image + Badge */}
              <div className="relative h-60 w-full bg-gray-100">
                <img
                  src={product.productImage[0]}
                  alt={product.productName}
                  className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-105"
                />
                {product?.isNew && (
                  <span className="absolute top-2 left-2 bg-purple-100 text-purple-800 text-xs font-semibold px-2 py-1 rounded">
                    New Season
                  </span>
                )}
              </div>

              {/* Content */}
              <div className="p-3 flex flex-col gap-1 flex-grow">
                {/* Brand Name */}
                <h3 className="text-sm font-semibold text-black line-clamp-1">
                  {product.brandName || 'Brand Name'}
                </h3>

                {/* Product Title */}
                <p className="text-sm text-gray-700 line-clamp-2">{product.productName}</p>

                {/* Pricing */}
                <div className="flex items-center gap-2 mt-1">
                  <p className="text-black font-semibold text-sm">
                    {displayINRCurrency(product.sellingPrice)}
                  </p>
                  <p className="line-through text-gray-400 text-sm">
                    {displayINRCurrency(product.price)}
                  </p>
                  {discount > 0 && (
                    <p className="text-green-600 text-xs font-medium">
                      {discount.toFixed(0)}% off
                    </p>
                  )}
                </div>

                {/* Rating */}
                <div className="flex items-center gap-1 text-xs text-gray-700 mt-1">
                  <div className="bg-green-100 text-green-700 px-1.5 py-0.5 rounded flex items-center gap-1">
                    <span>4.{Math.floor(Math.random() * 10)}</span>
                    <i className="fas fa-star text-green-600 text-xs"></i>
                  </div>
                  <span>({Math.floor(Math.random() * 30) + 1})</span>
                </div>

                {/* Stock Notice */}
                <p className="text-xs text-red-600 font-medium mt-1">Limited stock!</p>

                {/* Add to Cart */}
                <button
                  className="mt-2 w-full bg-red-600 hover:bg-red-700 text-white text-sm py-2 rounded-full transition-colors"
                  onClick={(e) => handleAddToCart(e, product._id)}
                >
                  Add to Cart
                </button>
              </div>
            </Link>
          )
        })}
      </div>
    </div>
  )
}

export default CategoryWiseProductDisplay
