import React from "react";

const IndianwearCards = () => {
  const categories = [
    {
        //title: "FUSION SETS",
        subtitle: "UP TO 70% OFF",
        image: "https://assets.tatacliq.com/medias/sys_master/images/63588843225118.png",
      },
      {
        title: "SILK SAREES",
        subtitle: "UP TO 70% OFF",
        image: "https://assets.tatacliq.com/medias/sys_master/images/63588843159582.png",
      },
      {
        title: "SHARARAS",
        subtitle: "UP TO 70% OFF",
        image: "https://assets.tatacliq.com/medias/sys_master/images/63588842897438.png",
      },
      {
        title: "EMBROIDERED KURTAS",
        subtitle: "UP TO 70% OFF",
        image: "https://assets.tatacliq.com/medias/sys_master/images/63588842962974.png",
      },
  ];

  return (
    <div className="mt-8 px-4 max-w-screen-lg mx-auto">
    <div className="flex justify-center gap-4">
      {categories.map((item, index) => (
        <div
          key={index}
          className="flex-1 max-w-[250px] rounded-2xl overflow-hidden relative group cursor-pointer"
        >
          <img
            src={item.image}
            alt={item.title}
            className="w-full h-[300px] object-cover transition-transform duration-300 group-hover:scale-105"
          />
          <div className="absolute bottom-0 w-full bg-gradient-to-t from-black/70 to-transparent px-2 py-3">
            <h3 className="text-white text-center text-sm font-semibold">
              {item.title}
            </h3>
          </div>
        </div>
      ))}
    </div>
  </div>
);
};


export default IndianwearCards;
