import React, { useContext, useEffect, useState } from 'react'
import SummaryApi from '../common'
import Context from '../context'
import displayINRCurrency from '../helpers/displayCurrency'
import { MdDelete } from "react-icons/md";
import { loadStripe } from '@stripe/stripe-js';

const Cart = () => {
    const [data, setData] = useState([])
    const [loading, setLoading] = useState(false)
    const context = useContext(Context)
    const loadingCart = new Array(4).fill(null)

    const fetchData = async () => {
        const response = await fetch(SummaryApi.addToCartProductView.url, {
            method: SummaryApi.addToCartProductView.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
        })
        const responseData = await response.json()
        if (responseData.success) {
            setData(responseData.data)
        }
    }

    const handleLoading = async () => {
        await fetchData()
    }

    useEffect(() => {
        setLoading(true)
        handleLoading().finally(() => setLoading(false)) // CHANGE: Ensure loading is set to false after fetch
    }, [])

    const increaseQty = async (id, qty) => {
        const response = await fetch(SummaryApi.updateCartProduct.url, {
            method: SummaryApi.updateCartProduct.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                _id: id,
                quantity: qty + 1
            })
        })

        const responseData = await response.json()
        if (responseData.success) {
            fetchData()
        }
    }

    const decraseQty = async (id, qty) => {
        if (qty >= 2) {
            const response = await fetch(SummaryApi.updateCartProduct.url, {
                method: SummaryApi.updateCartProduct.method,
                credentials: 'include',
                headers: {
                    "content-type": 'application/json'
                },
                body: JSON.stringify({
                    _id: id,
                    quantity: qty - 1
                })
            })

            const responseData = await response.json()
            if (responseData.success) {
                fetchData()
            }
        }
    }

    const deleteCartProduct = async (id) => {
        const response = await fetch(SummaryApi.deleteCartProduct.url, {
            method: SummaryApi.deleteCartProduct.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                _id: id,
            })
        })

        const responseData = await response.json()
        if (responseData.success) {
            fetchData()
            context.fetchUserAddToCart()
        }
    }

    const handlePayment = async () => {
        // console.log("process.env.REACT_APP_STRIPE_PUBLIC_KEY", process.env.REACT_APP_STRIPE_PUBLIC_KEY);
        const stripePromise = await loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY)
        const response = await fetch(SummaryApi.payment.url, {
            method: SummaryApi.payment.method,
            credentials: 'include',
            headers: {
                "content-type": 'application/json'
            },
            body: JSON.stringify({
                cartItems: data
            })
        })
        const responseData = await response.json()
        if (responseData?.id) {
            stripePromise.redirectToCheckout({ sessionId: responseData.id })
        }
        // console.log("payment response", responseData)
    }

    // CHANGE: Use optional chaining for sellingPrice
    const totalQty = data.reduce((previousValue, currentValue) => previousValue + currentValue.quantity, 0)
    const totalPrice = data.reduce((preve, curr) => preve + (curr.quantity * (curr?.productId?.sellingPrice || 0)), 0) // CHANGE

    return (
       <div className="container mx-auto px-4 py-6">
  <h1 className="text-3xl font-bold mb-6 text-gray-800">My Bag</h1>
  <div className="flex flex-col lg:flex-row gap-6">
    {/* Left - Product List */}
    <div className="flex-1">
      {data.map((product, index) => (
        <div key={index} className="flex border rounded-lg mb-4 bg-white shadow-sm">
          <div className="w-32 h-32 bg-gray-100 flex items-center justify-center">
            <img
              src={product?.productId?.productImage?.[0]}
              alt="product"
              className="w-full h-full object-contain mix-blend-multiply"
            />
          </div>
          <div className="flex-1 p-4 relative">
            <button onClick={() => deleteCartProduct(product?._id)} className="absolute top-3 right-3 text-red-500 hover:text-white hover:bg-red-500 p-2 rounded-full">
              <MdDelete />
            </button>
            <h2 className="text-lg font-semibold text-gray-800 line-clamp-1">{product?.productId?.productName}</h2>
            <p className="text-sm text-gray-500">{product?.productId?.category}</p>
            <div className="flex items-center justify-between mt-1">
              <span className="text-green-600 font-medium">{displayINRCurrency(product?.productId?.sellingPrice)}</span>
              <span className="text-gray-700 font-semibold">{displayINRCurrency(product?.productId?.sellingPrice * product?.quantity)}</span>
            </div>
            <div className="flex gap-2 mt-2">
              <button className="border border-red-600 text-red-600 w-6 h-6 rounded hover:bg-red-600 hover:text-white" onClick={() => decraseQty(product?._id, product?.quantity)}>-</button>
              <span>{product?.quantity}</span>
              <button className="border border-red-600 text-red-600 w-6 h-6 rounded hover:bg-red-600 hover:text-white" onClick={() => increaseQty(product?._id, product?.quantity)}>+</button>
            </div>
          </div>
        </div>
      ))}
    </div>

    {/* Right - Summary Section */}
    {data.length > 0 && (
      <div className="w-full lg:w-96 space-y-4">
        {/* Delivery Address */}
        <div className="bg-white p-4 rounded shadow-sm border">
          <div className="flex justify-between">
            <p className="font-semibold text-gray-800">Deliver To</p>
            <button className="text-pink-600 text-sm font-medium">Change</button>
          </div>
          <p className="mt-1 text-sm font-medium text-gray-700">110001, Delhi</p>
        </div>

        {/* Coupon Box */}
        <div className="bg-white p-4 rounded shadow-sm border flex justify-between items-center cursor-pointer">
          <span className="font-medium text-gray-800">Check for Coupons</span>
          <span>➔</span>
        </div>

        {/* Summary Box */}
        <div className="bg-white rounded shadow-sm border overflow-hidden">
          <h2 className="bg-red-600 text-white p-4 text-lg font-semibold">Summary</h2>
          <div className="p-4 space-y-2 text-gray-700 text-sm font-medium">
            <div className="flex justify-between">
              <span>Quantity</span>
              <span>{totalQty}</span>
            </div>
            <div className="flex justify-between">
              <span>Total Price</span>
              <span>{displayINRCurrency(totalPrice)}</span>
            </div>
            <button
              className="w-full mt-3 bg-pink-600 hover:bg-pink-700 text-white font-semibold py-2 rounded"
              onClick={handlePayment}
            >
              Checkout
            </button>
          </div>
        </div>
      </div>
    )}
  </div>
</div>

    )
}

export default Cart
